package main.java.java.baseball;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.InputMismatchException;
import java.util.Scanner;


public class App {
    public static String createRandomNum(){
        String random="";
        while(random.length()<3){
            String temp=String.valueOf((int)(Math.random() *9 +1));
            if(!random.contains(temp)){random+=temp;}
        }
        //System.out.println("랜덤변수: "+random);
        return random;
    }

    public static int[] calculate(String random, String myNum){
        int[] count = new int[2];
        for(int i =0;i<random.length();i++){
            if (random.charAt(i)==myNum.charAt(i)){
                count[0]+=1;
            }else if(random.contains(myNum.substring(i,i+1))){
                count[1]+=1;
            }
        }
        return count;
    }

    public static int printResult(int[] result){
        String ans="";
        if(result[0]==3){return 1;}
        if(Arrays.stream(result).sum()>0){
            if(result[0]>0){ans+=result[0]+" 스트라이크 ";}
            if(result[1]>0){ans+=result[1]+" 볼";}
            System.out.println(ans);

        }else{
            System.out.println("낫싱");
        }
        return 0;
    }

    public static void play(){
        int key=0;
        Scanner sc = new Scanner(System.in);
        String random=createRandomNum();

        while(key==0){
            System.out.println("숫자를 입력해 주세요.");
            try{
                String myNum= String.valueOf(sc.nextInt());
                int[] result=calculate(random,myNum);
                key=printResult(result);
            }catch (InputMismatchException ime){
                sc=new Scanner(System.in);
                //ime.printStackTrace();
                System.out.println("---------------숫자만 입력가능합니다.---------------");
            }
        }

    }


    public static void main(String[] args) {
        Scanner sc= new Scanner(System.in);
        int key=1;
        do{
            play();
            System.out.println("3개의 숫자를 맞히셧습니다! 게임 종료.");
            System.out.println("게임을 새로 시작하려면 1, 종료하려면 2를 입력하세요.");
            key=sc.nextInt();
        }while(key==1);
        sc.close();
    }
}
